var clock=new Vue({el:"#clock",data:{time:"",date:""}}),week=["星期日","星期一","星期二","星期三","星期四","星期五","星期六"],timerID=setInterval(updateTime,1e3);function updateTime(){var e=new Date;clock.time=zeroPadding(e.getHours(),2)+":"+zeroPadding(e.getMinutes(),2)+":"+zeroPadding(e.getSeconds(),2),clock.date=zeroPadding(e.getFullYear(),4)+"-"+zeroPadding(e.getMonth()+1,2)+"-"+zeroPadding(e.getDate(),2)+" "+week[e.getDay()]}function zeroPadding(e,a){for(var t="",d=0;d<a;d++)t+="0";return(t+e).slice(-a)}updateTime();var reloadPage=$("#reload-btn");reloadPage.click(function(e){window.history.back()});